mapper.select:
with T01 as ( select T.*, L.name line_name, S.id shift_id, concat(S.name, ' (', S.code, ')') shift_name, I.name item_name, I.stop_group_id group_id, U.name employee, case when hour(T.start_time) <= 6 then DATE_FORMAT(DATE_SUB(T.start_time, INTERVAL 1 DAY) , '%Y-%m-%d') else DATE_FORMAT(T.start_time, '%Y-%m-%d') end issue_date from stop_times T left join line L on T.line = L.id left join shifts S on T.shift = S.id left join stop_items I on T.stop_item_id = I.id left join users U on T.user_code = U.username ), T02 as ( select G.id group_id, G.name group_name, T.id type_id, T.name type_name from stop_groups G left join stop_types T on G.stop_type_id = T.id ), T03 as ( select T01.id, T01.stop_item_id item_id, T01.item_name, T01.line line_id, T01.line_name, T01.start_time, T01.stop_time, T01.remark, T01.created_at, T01.shift_id, T01.model, T01.shift_name, T01.employee, T02.*, T01.issue_date date from T01 left join T02 on T01.group_id = T02.group_id ) select T03.*, timestampdiff(minute,T03.start_time, T03.stop_time) total_stop_time from T03 where 1 = 1
and T03.date between DATE_FORMAT(#{fromDate}, '%Y-%m-%d') and DATE_FORMAT(#{toDate}, '%Y-%m-%d')
and T03.line_id = #{line}
and T03.shift_id = #{shift}
and T03.type_id = #{type}
and T03.group_id = #{group}
and DATE_FORMAT(T03.start_time, '%Y-%m-%d') between DATE_FORMAT(#{startTimeRange[0]}, '%Y-%m-%d') and DATE_FORMAT(#{startTimeRange[1]}, '%Y-%m-%d')
and DATE_FORMAT(T03.created_at, '%Y-%m-%d') between DATE_FORMAT(#{createdAtRange[0]}, '%Y-%m-%d') and DATE_FORMAT(#{createdAtRange[1]}, '%Y-%m-%d')
order by isnull(T03.stop_time) desc, T03.id desc
